interface IVault {
    // ============ Event Definitions ============
    event WhitelistAdded(address indexed addr);
    event WhitelistRemoved(address indexed addr);
    event WhitelistStatusChanged(bool enabled);
    event TokenPaused();
    event TokenUnpaused();
