pragma solidity 0.8.26;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "../interface/AggregatorV3Interface.sol";

/**
 * @author  Accelerate Finance
 * @title   PriceFeed
 * @dev     Implements a simple price feed mechanism with role-based access control.
 * @notice  The contract follows the AggregatorV3Interface and allows authorized users to update price data.
 */
contract PriceFeed is AggregatorV3Interface, AccessControl {
    struct RoundData {
        int256 answer;
        uint256 startedAt;
        uint256 updatedAt;
        uint80 answeredInRound;
    }

    bytes32 public constant MANAGER_ROLE = keccak256("MANAGER_ROLE");
    bytes32 public constant FEEDER_ROLE = keccak256("FEEDER_ROLE");
    // Mapping to store round data, where the key is the round ID and the value is a RoundData struct
    mapping(uint80 => RoundData) private rounds;
    // Stores the latest round ID, which increments each time new data is added
    uint80 private latestRoundId;

    constructor(address manager) {
        _grantRole(DEFAULT_ADMIN_ROLE, manager);
        _grantRole(MANAGER_ROLE, manager);
        _setRoleAdmin(FEEDER_ROLE, MANAGER_ROLE);
    }

    /**
     * @notice  Allows a user with the FEEDER_ROLE to add a new price entry.
     * @dev     Only addresses with FEEDER_ROLE can call this function
     * @param   price  The new price value to store.
     * @param   priceTime  Timestamp of the price update.
     */
    function addPrice(
        int256 price,
        uint256 priceTime
    ) public onlyRole(FEEDER_ROLE) {
        _addPrice(price, priceTime);
    }

    /**
     * @notice  Returns the description of the price feed.
     * @return  string  A string representing the price feed pair.
     */
    function description() public pure override returns (string memory) {
        return "RBFToken/USD";
    }

    /**
     * @notice  Returns the version of the contract.
     * @return  uint256  The version number as a uint256.
     */
    function version() public pure override returns (uint256) {
        return 1;
    }

    /**
     * @notice  Returns the number of decimal places used for the price values.
     * @return  uint8  The number of decimals.
     */
    function decimals() public pure override returns (uint8) {
        return 8;
    }

    /**
     * @notice  Retrieves price data for a specific round.
     * @param   roundId The round ID to query.
     * @return  uint80  roundId The queried round ID.
     * @return  int256  answer The price recorded for this round.
     * @return  uint256  startedAt Timestamp when the round started.
     * @return  uint256  updatedAt Timestamp when the price was last updated.
     * @return  uint80  answeredInRound The round ID for which the price was answered.
     */
    function getRoundData(
        uint80 roundId
    ) public view override returns (uint80, int256, uint256, uint256, uint80) {
        require(rounds[roundId].updatedAt > 0, "No data for this round");
        RoundData memory round = rounds[roundId];
        return (
            roundId,
            round.answer,
            round.startedAt,
            round.updatedAt,
            round.answeredInRound
        );
    }

    /**
     * @notice  Retrieves the latest round data.
     * @return  uint80  latestRoundId The most recent round ID.
     * @return  int256  answer The price recorded in the latest round.
     * @return  uint256  startedAt Timestamp when the latest round started.
     * @return  uint256  updatedAt Timestamp when the latest price update occurred.
     * @return  uint80   answeredInRound The round ID for which the latest price was answered.
     */
    function latestRoundData()
        external
        view
        override
        returns (uint80, int256, uint256, uint256, uint80)
    {
        require(latestRoundId > 0, "No price data available");
        RoundData memory round = rounds[latestRoundId];
        return (
            latestRoundId,
            round.answer,
            round.startedAt,
            round.updatedAt,
            round.answeredInRound
        );
    }

    function _addPrice(int256 price, uint256 priceTime) internal {
        latestRoundId++;
        rounds[latestRoundId] = RoundData({
            answer: price,
            startedAt: priceTime,
            updatedAt: priceTime,
            answeredInRound: latestRoundId
        });

        emit PriceUpdated(latestRoundId, price, priceTime);
    }
}

contracts/rbf/RBF.sol.content:
// SPDX-License-Identifier: MIT
/**
    ___                         __                         __
   /   |  _____  _____  ___    / /  ___    _____  ____ _  / /_  ___
  / /| | / ___/ / ___/ / _ \  / /  / _ \  / ___/ / __ `/ / __/ / _ \
 / ___ |/ /__  / /__  /  __/ / /  /  __/ / /    / /_/ / / /_  /  __/
/_/  |_|\___/  \___/  \___/ /_/   \___/ /_/     \__,_/  \__/  \___/

*/
pragma solidity 0.8.26;

import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/IERC20MetadataUpgradeable.sol";
import "../vault/Vault.sol";
import "../interface/AggregatorV3Interface.sol";
import "../interface/IRBF.sol";

struct RBFInitializeData {
    string name; // The name of the RBF contract
    string symbol; // The symbol of the RBF contract (usually a ticker like "RBF")
    address assetToken; // The address of the ERC20 token used in the RBF contract (e.g., USDC, ETH)
    address depositTreasury; // The address that receives the deposits from the vault
    address dividendTreasury; // The address where dividends (profits) will be stored and distributed
    address priceFeed; // The address of the price feed (e.g., Chainlink) to get the asset price for RBF calculations
    uint256 mintSlippageBps; // The slippage tolerance for minting RBF tokens
    address manager; // The address of the contract manager who can dividend in the RBF contract
}

/**
 * @author  Accelerate Finance
 * @title   RBF
 * @dev     A contract for handling deposit and minting of RBF tokens, managing dividends, and controlling access by the manager.
 * @notice  This contract allows deposits in an underlying asset token and mints a corresponding amount of RBF tokens based on the deposit and the asset's price. It also supports dividend distribution and fee management by manager.
 */
contract RBF is
    IRBF,
    OwnableUpgradeable,
    ERC20Upgradeable,
    AccessControlUpgradeable
{
    using SafeERC20 for IERC20;
    uint256 public constant BPS_DENOMINATOR = 10_000;
    bytes32 public constant MANAGER_ROLE = keccak256("MANAGER_ROLE");
    bytes32 public constant PRICE_MINT_AMOUNT_SETTER_ROLE =
        keccak256("PRICE_MINT_AMOUNT_SETTER_ROLE");
    // The address of the asset token that this contract interacts with (e.g., an ERC-20 token).
    address public assetToken;
    // The address of the treasury that holds deposited assets.
    address public depositTreasury; 
    // The address of the treasury responsible for distributing dividends to rbfholders.
    address public dividendTreasury;
    // The price feed contract used to fetch price data for the asset.  
    AggregatorV3Interface public priceFeed;
    // The address of the manager who has administrative privileges over the contract.
    address public manager;
    // The slippage tolerance for minting RBF tokens.
    uint256 public mintSlippageBps;  
    // The address of the vault  which can deposit assetToken.
    address public vault;
    // The amount of assetToken deposited into the depositTreasury.
    uint256 public depositAmount;
    // The price of RBF tokens minted for each assetToken deposited.
    uint256 public depositPrice;
    // The amount of RBF tokens minted for assetToken deposited.
    uint256 public depositMintAmount;
    // Multiplier to adjust decimals between assetToken and RBF token
    uint256 public decimalsMultiplier;
    // The URI of the rbf token. 
    string public tokenURI;

    modifier onlyVault() {
        require(msg.sender == vault, "RBF: you are not vault");
        _;
    }

    /**
     * @dev  Constructor function to disable initializers
     */
    constructor() {
        _disableInitializers();
    }

    /**
     * @notice  Initializes the RBF contract with required parameters.
     * @param   data  Initialization data containing contract configuration.
     */
    function initialize(RBFInitializeData memory data) public initializer {
        __ERC20_init(data.name, data.symbol);
        __Ownable_init();

        require(
            data.assetToken != address(0),
            "RBF: assetToken address cannot be zero address" //tc-14:assetToken address is zero address
        );
        assetToken = data.assetToken;
        require(
            data.depositTreasury != address(0),
            "RBF: depositTreasury address cannot be zero address" //tc-15:depositTreasury address is zero address
        );
        depositTreasury = data.depositTreasury;
        require(
            data.dividendTreasury != address(0),
            "RBF: dividendTreasury address cannot be zero address" //tc-16:dividendTreasury address is zero address
        );
        dividendTreasury = data.dividendTreasury;
        require(
            data.priceFeed != address(0),
            "RBF: priceFeedAddr can not be zero address" //tc-17:priceFeed address is zero address
        );
        priceFeed = AggregatorV3Interface(data.priceFeed);
        require(
            data.manager != address(0),
            "RBF: manager address can not be zero address" //tc-18:manager address is zero address
        );
        manager = data.manager;
        mintSlippageBps = data.mintSlippageBps;

        decimalsMultiplier =
            10 **
                (decimals() -
                    IERC20MetadataUpgradeable(data.assetToken).decimals());

        _grantRole(DEFAULT_ADMIN_ROLE, data.manager);
        _grantRole(MANAGER_ROLE, data.manager);
        _setRoleAdmin(PRICE_MINT_AMOUNT_SETTER_ROLE, MANAGER_ROLE);
    }

    /*//////////////////////////////////////////////////////////////
                  Deposit/Dividend Functions
    //////////////////////////////////////////////////////////////*/
    /**
     * @notice  Deposits assetToken into the RBF contract.
     * @dev     This function is only callable by the Vault.
     * @param   amount The amount of assetToken to deposit.
     */
    function requestDeposit(uint256 amount) public onlyVault { //tc-66:VaultrequestDepositVaultrequestDeposit
        require(
            IERC20(assetToken).balanceOf(msg.sender) >= amount,
            "RBF: Insufficient balance"
        );
        depositAmount += amount;
        SafeERC20.safeTransferFrom(
            IERC20(assetToken),
            msg.sender,
            depositTreasury,
            amount
        );
        emit DepositEvent(msg.sender,amount);
    }

    /**
     * @notice  Allows the manager to claim the deposit and mint RBF tokens.
     * @dev     This function is only callable by the manager role.
     */
    function claimDeposit() public onlyRole(MANAGER_ROLE) { //tc-66:MANAGER_ROLEclaimDeposit;MANAGER_ROLEclaimDeposit
        require(depositAmount > 0, "RBF: depositAmount must be greater than 0"); //tc-70:depositAmount0claimDeposit
        require(depositPrice > 0, "RBF: depositPirce must be greater than 0"); //tc-66:depositPirce0claimDeposit;
        require(
            depositMintAmount > 0,
            "RBF: depositMintAmount must be greater than 0" //tc-66:depositMintAmount0claimDeposit
        );
        uint256 scaledDeposit = _scaleUp(depositAmount);
        uint256 minAmount = (scaledDeposit * (10 ** priceFeed.decimals())) /
            depositPrice;
        uint256 slippageFactor = (mintSlippageBps * minAmount) / BPS_DENOMINATOR;
        uint256 lowerBound = minAmount - slippageFactor;
        uint256 upperBound = minAmount + slippageFactor;
        require(
            depositMintAmount >= lowerBound && depositMintAmount <= upperBound,
            "RBF: depositMintAmount is not in the range" //tc-71:depositMintAmountclaimDeposit
        );
        _mint(vault, depositMintAmount);
        depositAmount = 0;
    }

    /**
     * @notice  Allows the manager to distribute dividends from the dividend treasury to the vault.
     * @dev     This function calculates the dividend share for the vault and transfers the dividend amount.
     */
    function dividend() public onlyRole(MANAGER_ROLE) {//tc-66:MANAGER_ROLEdividend;MANAGER_ROLEdividend
        uint256 totalDividend = IERC20(assetToken).balanceOf(dividendTreasury);
        require(totalDividend > 0, "RBF: totalDividend must be greater than 0"); //tc-66:totalDividend0dividend
        uint256 totalSupply = totalSupply();
        require(totalSupply > 0, "RBF: totalSupply must be greater than 0");//
        require(vault != address(0), "RBF: vault can not be zero address");
        require(
            balanceOf(vault) > 0,
            "RBF: vault balance must be greater than 0"
        );
        address vaultDividendTreasury = Vault(vault).dividendTreasury();
        require(
            vaultDividendTreasury != address(0),
            "RBF: vault dividendTreasury cant not be zero"
        );
        _dividend(
            balanceOf(vault),
            totalSupply,
            totalDividend,
            vaultDividendTreasury
        );
    }

    /**
     * @notice  Sets the deposit price and mint amount for RBF tokens.
     * @dev     This function is only callable by the PRICE_MINT_AMOUNT_SETTER_ROLE.
     * @param   _depositPrice    amount of assetToken deposited
     * @param   _depositMintAmount amount of RBF tokens minted
     */
    function setDepositPriceAndMintAmount(
        uint256 _depositPrice,
        uint256 _depositMintAmount
    ) public onlyRole(PRICE_MINT_AMOUNT_SETTER_ROLE) {
        require(depositAmount > 0, "RBF: depositAmount must be greater than 0");
        depositPrice = _depositPrice;
        depositMintAmount = _depositMintAmount;
        emit DepositDataEvent(depositPrice, depositMintAmount);
    }

    /**
     * @notice  Allows the manager to set the vault contract address.
     * @dev     This function assigns the vault address, which interacts with the RBF contract.
     * @param   _vault  The address of the vault to be set.
     */
    function setVault(address _vault) public onlyRole(MANAGER_ROLE) { //tc-66:MANAGER_ROLEsetVault;MANAGER_ROLEsetVault
        require(
            _vault != address(0),
            "RBF: vaultAddr cannot be zero address"
        );
        require(vault==address(0),"RBF: vaultAddr already set");
        vault = _vault;
        emit SetVault(_vault);
    }

    /**
     * @notice  Sets the mint slippage percentage in basis points (bps)
     * @dev     Only accounts with the MANAGER_ROLE can call this function
     * @param   _mintSlippageBps The new mint slippage value in basis points (bps)
     */
    function setMintSlippageBps(uint256 _mintSlippageBps) public onlyRole(MANAGER_ROLE) {
        mintSlippageBps=_mintSlippageBps;
        emit SetMintSlippageBps(mintSlippageBps);
    }


    /**
     * @notice  Sets the token metadata URI
     * @dev     This function can only be called by an address with the `MANAGER_ROLE` role.
     * @param   _tokenURI The new token URI (e.g., IPFS or a centralized server).
     */
    function setTokenURI(string memory _tokenURI) public onlyRole(MANAGER_ROLE) {
        tokenURI=_tokenURI;
        emit SetTokenURI(tokenURI);
    }



    /**
     * @notice  Fetches the net asset value (NAV) of the vault's RBF tokens based on the asset price.
     * @dev     This function calculates the NAV of the RBF tokens held by vault by fetching the latest price.
     * @return  uint256  The NAV in terms of the asset token's value.
     */
    function getAssetsNav() public view returns (uint256) {
        int256 lastPrice = getLatestPrice();
        uint256 amount = balanceOf(vault);
        uint256 indexDecimals = 10 ** priceFeed.decimals();
        return (amount * uint256(lastPrice)) / indexDecimals;
    }

    /**
     * @notice  Fetches the latest price of the asset token from the price feed.
     * @dev     This function interacts with the price feed contract to get the latest price of the asset token.
     * @return  int256  The latest price of the asset token.
     */
    function getLatestPrice() public view returns (int256) {
        (
            uint80 roundId,
            int256 price,
            uint256 startedAt,
            uint256 updatedAt,
            uint80 answeredInRound
        ) = priceFeed.latestRoundData();
        require(
            (roundId > 0 &&
                price > 0 &&
                startedAt > 0 &&
                updatedAt > 0 &&
                answeredInRound > 0),
            "Invalid price data"
        );
        return price;
    }

    /**
     * @notice  Overrides the decimals function to return 6 decimals for the RBF token.
     *          Same as Stablecoins decimals
     * @dev     Sets the precision of the RBF token to 6 decimals.
     * @return  uint8  The number of decimals for the RBF token.
     */
    function decimals() public view virtual override returns (uint8) {
        return 6;
    }

    function _dividend(
        uint256 rbfAmount,
        uint256 totalSupply,
        uint256 totalDividend,
        address receiver
    ) internal {
        uint256 dividendAmount = (rbfAmount * totalDividend) / totalSupply;
        require(
            dividendAmount > 0,
            "RBF: dividendAmount must greater than zero"
        );
        SafeERC20.safeTransferFrom(
            IERC20(assetToken),
            dividendTreasury,
            receiver,
            dividendAmount
        );
    }

    function _scaleUp(uint256 amount) internal view returns (uint256) {
        return amount * decimalsMultiplier;
    }
}

contracts/rbf/RBFRouter.sol.content:
// SPDX-License-Identifier: MIT
/**
    ___                         __                         __
   /   |  _____  _____  ___    / /  ___    _____  ____ _  / /_  ___
  / /| | / ___/ / ___/ / _ \  / /  / _ \  / ___/ / __ `/ / __/ / _ \
 / ___ |/ /__  / /__  /  __/ / /  /  __/ / /    / /_/ / / /_  /  __/
/_/  |_|\___/  \___/  \___/ /_/   \___/ /_/     \__,_/  \__/  \___/

*/
pragma solidity 0.8.26;

import "@openzeppelin/contracts/access/Ownable.sol";
import "../interface/IRBFFactory.sol";
import "../interface/IEscrowFactory.sol";
import "../interface/IPriceFeedFactory.sol";
import "../interface/IRBFRouter.sol";
import "../common/Escrow.sol";
import "./RBF.sol";

/**
 * @author  Accelerate Finance
 * @title   RBFRouter
 * @dev     Router contract for deploying RBF contracts and managing their settings.
 * @notice  This contract facilitates the creation and management of RBF contracts, handling escrow and price feed deployment.
 */
contract RBFRouter is IRBFRouter, Ownable {
    // Struct to store RBF contract information
    struct RBFInfo {
        uint256 createdAt; // Timestamp when the RBF contract was created
        address rbf; // Address of the deployed RBF contract
        address rbfProxyAdmin; // Address of the RBF proxy admin
        address rbfImpl; // Address of the RBF implementation contract
        address dividendTreasury; // Address of the dividend treasury escrow contract
        address priceFeed; // Address of the associated price feed contract
    }

    // Struct to hold data for deploying an RBF contract
    struct RBFDeployData {
        uint64 rbfId; // Unique identifier for the RBF contract
        string name; //Name of the RBF token
        string symbol; //Symbol of the RBF token
        address assetToken; //Address of the asset backing the RBF
        address depositTreasury; //Address of the deposit treasury
        uint256 mintSlippageBps; // Mint slippage tolerance for RBF
        address deployer; //Address of the deployer
        address manager; //Address of the RBF and PriceFeed manager
        address guardian; //Guardian address for security proxy update
    }

    // Minimum number of valid signatures required for deployment
    uint256 public threshold;
    // Nonce for tracking RBF contract deployments
    uint64 public rbfNonce;
    // Immutable factory contract addresses for RBF, escrow, and price feeds
    IRBFFactory public immutable rbfFactory;
    IEscrowFactory public immutable escrowFactory;
    IPriceFeedFactory public immutable priceFeedFactory;
    // Mapping of RBF ID to its corresponding contract information
    mapping(uint64 => RBFInfo) private rbfs;
    // Mapping to track whitelisted addresses authorized to sign transactions
    mapping(address => bool) public whiteListed;
    address[] public whiteLists;

    /**
     * @notice Constructor to initialize the router with necessary parameters.
     * @dev Sets up the factories for RBF, escrow, and price feed while initializing the whitelist and threshold.
     * @param _whiteLists Array of addresses that are whitelisted for signing transactions.
     * @param _threshold Minimum number of valid signatures required.
     * @param _rbfFactory Address of the RBF factory.
     * @param _escrowFactory Address of the escrow factory.
     * @param _priceFeedFactory Address of the price feed factory.
     */
    constructor(
        address[] memory _whiteLists,
        uint256 _threshold,
        address _rbfFactory,
        address _escrowFactory,
        address _priceFeedFactory
    ) Ownable() {
        require(_whiteLists.length > 0, "whiteLists must not be empty");
        whiteLists = _whiteLists;
        for (uint256 i = 0; i < _whiteLists.length; i++) {
            whiteListed[_whiteLists[i]] = true;
        }
        require(_threshold > 0, "threshold must >0");
        threshold = _threshold;
        require(_rbfFactory != address(0), "rbfFactory must not be zero");
        rbfFactory = IRBFFactory(_rbfFactory);
        require(_escrowFactory != address(0), "escrowFactory must not be zero");
        escrowFactory = IEscrowFactory(_escrowFactory);
        require(
            _priceFeedFactory != address(0),
            "priceFeedFactory must not be zero"
        );
        priceFeedFactory = IPriceFeedFactory(_priceFeedFactory);
    }


    /**
     * @notice  Updates the whitelist of authorized signers and the signature threshold.
     * @dev     Only the contract owner can call this function. It first clears the existing whitelist,
     *          then sets the new whitelist addresses and updates the required signature threshold.
     * @param   _whiteLists  Array of new addresses to be whitelisted.
     * @param   _threshold  Minimum number of valid signatures required for verification.
     */
     //tc-74:
    function setWhiteListsAndThreshold(
        address[] memory _whiteLists,
        uint256 _threshold
    ) public onlyOwner {
        require(_whiteLists.length > 0, "whiteLists must not be empty");//tc-75
        require(_threshold > 0, "threshold must not be zero");//tc-76
        // Remove existing whitelist addresses
        uint oldLen=whiteLists.length;
        for (uint i = 0; i < oldLen; i++) {
            whiteListed[whiteLists[i]] = false;
        }

        // Update whitelist addresses
        whiteLists = _whiteLists;
        uint newLen=_whiteLists.length;
        for (uint256 i = 0; i < newLen; i++) {
            whiteListed[_whiteLists[i]] = true;
        }
        // Update the required signature threshold
        threshold = _threshold;

        emit SetWhiteListsAndThreshold(_whiteLists, _threshold);
    }

    /**
     * @notice  Deploys a new RBF contract after verifying signatures.
     * @dev     Decodes the deployment data, verifies signatures, and deploys the RBF contract along with escrow and price feed.
     * @param   deployData  Encoded data containing deployment parameters.
     * @param   signatures  Array of signatures for verification.
     */
     //tc-1:RBFRouterRBF
     //tc-4:RBFRouterEscrowFactoryRBF
     //tc-7:RBFRouterEscrowFactoryPriceFeedFactoryRBF
     //tc-10:RBFRouterRBFFactoryEscrowFactoryPriceFeedFactoryRBF
    function deployRBF(
        bytes memory deployData,
        bytes[] memory signatures
    ) public {
        _verifySign(deployData, signatures);

        RBFDeployData memory rbfDeployData = abi.decode(
            deployData,
            (RBFDeployData)
        );
        require(rbfDeployData.rbfId == rbfNonce, "RBFRouter:Invalid rbfId"); //tc-13
        require(
            rbfDeployData.deployer == msg.sender,
            "RBFRouter:Invalid deployer" //tc-12
        );
        rbfNonce++;
        address dividendTreasury = escrowFactory.newEscrow(address(this));
        address pricerFeed = priceFeedFactory.newPriceFeed(
            rbfDeployData.manager
        );
        RBFInitializeData memory data = RBFInitializeData({
            name: rbfDeployData.name,
            symbol: rbfDeployData.symbol,
            assetToken: rbfDeployData.assetToken,
            depositTreasury: rbfDeployData.depositTreasury,
            dividendTreasury: dividendTreasury,
            priceFeed: pricerFeed,
            mintSlippageBps: rbfDeployData.mintSlippageBps,
            manager: rbfDeployData.manager
        });
        (address rbf, address rbfProxyAdmin, address rbfImpl) = rbfFactory
            .newRBF(data, rbfDeployData.guardian);
        rbfs[rbfDeployData.rbfId] = RBFInfo(
            block.timestamp,
            rbf,
            rbfProxyAdmin,
            rbfImpl,
            address(dividendTreasury),
            address(pricerFeed)
        );
        Escrow(dividendTreasury).approveMax(rbfDeployData.assetToken, rbf);
        Escrow(dividendTreasury).rely(address(rbf));
        Escrow(dividendTreasury).deny(address(this));
        RBF(rbf).transferOwnership(msg.sender);
        emit DeployRBFEvent(
            rbfDeployData.rbfId,
            pricerFeed,
            rbf,
            dividendTreasury
        );
    }

    function _verifySign(
        bytes memory deployData,
        bytes[] memory signatures
    ) internal view {
        bytes32 ethSignedMessageHash = getEthSignedMessageHash(
            keccak256(deployData)
        );
        uint256 validSignatures = 0;
        for (uint256 i = 0; i < signatures.length; i++) {
            address signer = recoverSigner(ethSignedMessageHash, signatures[i]);
            require(whiteListed[signer], "RBFRouter:Invalid Signer"); //tc-11
            validSignatures++;
        }
        require(validSignatures >= threshold, "RBFRouter:Invalid Threshold");
    }

    /**
     * @notice  Retrieves information about a deployed RBF contract.
     * @param   rbfId  rbfId The unique identifier of the RBF contract.
     * @return  RBFInfo  The corresponding RBFInfo struct.
     */
    function getRBFInfo(uint64 rbfId) public view returns (RBFInfo memory) {
        return rbfs[rbfId];
    }

    /**
     * @notice  Returns the length of the whiteLists array.
     * @dev     This function is used to get the length of the whiteLists array.
     * @return  uint256  The length of the whiteLists array.
     */
    function getWhiteListsLen() public view returns (uint256) {
        return whiteLists.length;
    }


    function recoverSigner(
        bytes32 ethSignedMessageHash,
        bytes memory signature
    ) internal pure returns (address) {
        require(signature.length == 65, "RBFRouter:Invalid signature length");

        bytes32 r;
        bytes32 s;
        uint8 v;

        //  r, s, v
        assembly {
            r := mload(add(signature, 0x20))
            s := mload(add(signature, 0x40))
            v := byte(0, mload(add(signature, 0x60)))
        }

        return ecrecover(ethSignedMessageHash, v, r, s);
    }

    function getEthSignedMessageHash(
        bytes32 messageHash
    ) internal pure returns (bytes32) {
        return
            keccak256(
                abi.encodePacked(
                    "\x19Ethereum Signed Message:\n32",
                    messageHash
                )
            );
    }
}

