// SPDX-License-Identifier: MIT
pragma solidity ^0.8.26;

/**
 * @title ICreation
 * @dev  - 
 */
interface ICreation {
    // ============  ============

    /**
     * @dev Vault
     */
    struct VaultInitData {
        address validator;        // 
        bool whitelistEnabled;    // 
        address[] initialWhitelist; // 
    }

    /**
     * @dev Token
     */
    struct TokenInitData {
        string name;      // 
        string symbol;    // 
        uint8 decimals;   // 
    }

    /**
     * @dev Fund
     */
    struct FundInitData {
        uint256 startTime;           // UNIX
        uint256 endTime;             // UNIX
        address assetToken;          // USDT
        uint256 maxSupply;           // 
        uint256 softCap;             // 
        uint256 sharePrice;          // 
        uint256 minDepositAmount;    // 
        uint256 manageFeeBps;        // 200 = 2%
        address fundingReceiver;     // 
        address manageFeeReceiver;   // 
        uint256 decimalsMultiplier;  // 10^
    }

    /**
     * @dev Dividend
     */
    struct DividendInitData {
        address rewardToken;   // USDT
        address rewardManager; // 
    }

    /**
     * @dev 
     */
    struct DeploymentResult {
        address vault;
        address token;
        address fund;
        address accumulatedYield;
    }

    /**
     * @dev 
     */
    struct Project {
        string name;
        address vault;
        address token;
        address fund;
        address accumulatedYield;
        uint256 createdAt;
        address deployer;
    }

    // ============  ============

    event VaultCreated(address indexed vault);
    event TokenCreated(address indexed token);
    event FundCreated(address indexed fund);
    event YieldCreated(address indexed yield);


    event ProjectCreated(
        string name,
        address vault,
        address token,
        address fund,
        address yield,
        address deployer
        );

    event FactoriesUpdated(
        address vaultFactory,
        address tokenFactory,
        address fundFactory,
        address dividendFactory
    );

    // ============  ============

    /**
     * @dev 
     * @param _vaultFactory Vault
     * @param _tokenFactory Token
     * @param _fundFactory Fund
     * @param _dividendFactory Dividend
     */
    function setFactories(
        address _vaultFactory,
        address _tokenFactory,
        address _fundFactory,
        address _dividendFactory
    ) external;

    /**
     * @dev 
     * @return vaultFactory Vault
     * @return tokenFactory Token
     * @return fundFactory Fund
     * @return dividendFactory Dividend
     */
    function getFactories() external view returns (
        address vaultFactory,
        address tokenFactory,
        address fundFactory,
        address dividendFactory
    );

    // ============  ============

    /**
     * @dev bytes
     * @param projectName 
     * @param vaultTemplateId VaultID
     * @param vaultInitData Vaultbytes
     * @param tokenTemplateId TokenID
     * @param tokenInitData Tokenbytes
     * @param fundTemplateId FundID
     * @param fundInitData Fundbytes
     * @param dividendTemplateId DividendID
     * @param dividendInitData Dividendbytes
     * @return result 
     */
    function deployAll(
        // 
        string memory projectName,

        // Vault
        uint256 vaultTemplateId,
        bytes memory vaultInitData,

        // Token
        uint256 tokenTemplateId,
        bytes memory tokenInitData,

        // Fund
        uint256 fundTemplateId,
        bytes memory fundInitData,

        // Dividend
        uint256 dividendTemplateId,
        bytes memory dividendInitData
    ) external returns (DeploymentResult memory result);



    /**
     * @dev Vault
     * @param templateId ID
     * @param initData bytes
     * @return vault Vault
     */
    function deployVault(
        uint256 templateId,
        bytes memory initData
    ) external returns (address vault);

    /**
     * @dev Token
     * @param templateId ID
     * @param vault Vault
     * @param initData bytes
     * @return token Token
     */
    function deployToken(
        uint256 templateId,
        address vault,
        bytes memory initData
    ) external returns (address token);

    /**
     * @dev Fund
     * @param templateId ID
     * @param vault Vault
     * @param initData bytes
     * @return fund Fund
     */
    function deployFund(
        uint256 templateId,
        address vault,
        bytes memory initData
    ) external returns (address fund);

    /**
     * @dev Dividend
     * @param templateId ID
     * @param vault Vault
     * @param token Token
     * @param initData bytes
     * @return dividend Dividend
     */
    function deployDividend(
        uint256 templateId,
        address vault,
        address token,
        bytes memory initData
    ) external returns (address dividend);

    // ============  ============



    /**
     * @dev 
     * @param user 
     * @return projects 
     */
    function getUserProjects(address user) external view returns (DeploymentResult[] memory projects);

    /**
     * @dev 
     * @param projectName 
     * @return project 
     */
    function getProjectByName(string memory projectName) external view returns (Project memory);

    /**
     * @dev 
     * @param user 
     * @return projects 
     */
    function getUserProjectDetails(address user) external view returns (Project[] memory);
}

contracts/v2/interfaces/ICrowdsale.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.26;

/**
 * @title ICrowdsale
 * @dev Crowdsale module interface
 */
interface ICrowdsale {
    // ============ Struct Definitions ============

    /**
     * @dev On-chain signature data structure
     */
    struct OnChainSignatureData {
        string operation;        // Operation name ("deposit", "redeem")
        uint256 amount;          // Deposit amount
        address receiver;        // Receiver address
        uint256 nonce;          // Nonce to prevent replay attacks
        uint256 chainId;        // Chain ID to prevent cross-chain replay
        address contractAddress; // Contract address to prevent replay
    }

    /**
     * @dev Off-chain signature data structure
     */
    struct OffChainSignatureData {
        uint256 amount;          // amount
        address receiver;        // Receiver address
    }

    // ============ Events ============
    event Deposit(address indexed receiver, uint256 assertAmount, uint256 manageFee, uint256 shares);
    event FundFailRedeem(address redeemer,uint256 shares,uint256 assetAmount,uint256 feeAmount);
    event OffChainDeposit(address indexed receiver, uint256 assertAmount, uint256 shares, bytes signature);
    event OffChainRedeem(address indexed receiver, uint256 assertAmount);
    event FundingAssetsWithdrawn(address indexed receiver, uint256 amount);
    event ManageFeeWithdrawn(address indexed receiver, uint256 amount);
    event TokenUnpausedOnFundingSuccess();

    // ============ Basic Field Query Interface ============
    function vault() external view returns (address);
    function startTime() external view returns (uint256);
    function endTime() external view returns (uint256);
    function assetToken() external view returns (address);
    function maxSupply() external view returns (uint256);
    function softCap() external view returns (uint256);
    function sharePrice() external view returns (uint256);
    function minDepositAmount() external view returns (uint256);
    function manageFeeBps() external view returns (uint256);
    function fundingReceiver() external view returns (address);
    function manageFeeReceiver() external view returns (address);
    function decimalsMultiplier() external view returns (uint256);
    function manager() external view returns (address);
    function fundingAssets() external view returns (uint256);
    function manageFee() external view returns (uint256);

    // ============ Funding Operations Interface ============
    // User initiated, requires manager signature
    function deposit(uint256 amount, address receiver, bytes memory signature) external returns (uint256);

    // User initiated, requires manager signature - redeems all user shares
    function redeem(address receiver, bytes memory signature) external;

    // Backend manager initiated, requires DRDS signature verification
    function offChainDeposit(uint256 amount, address receiver, bytes memory drdsSignature) external;

    // Backend manager initiated - redeems all user shares
    function offChainRedeem(address receiver) external;

    // ============ Fund Management Interface ============
    function withdrawFundingAssets() external;
    function withdrawManageFee() external;
    function unpauseTokenOnFundingSuccess() external;

    // ============ Status Query Interface ============
    function isFundingSuccessful() external view returns (bool);
    function isFundingPeriodActive() external view returns (bool);
    function getTotalRaised() external view returns (uint256);
    function getRemainingSupply() external view returns (uint256);

    // ============ Signature Query Interface ============
    function getManagerNonce() external view returns (uint256);
    function getDepositSignatureMessage(uint256 amount, address receiver, uint256 nonce) external view returns (bytes32);
    function getRedeemSignatureMessage(uint256 amount, address receiver, uint256 nonce) external view returns (bytes32);

    // ============  ============
    function initiate(address _vault, bytes memory _initData) external;
}

