// SPDX-License-Identifier: MIT
pragma solidity ^0.8.26;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "../../interfaces/IToken.sol";
import "../../interfaces/IVault.sol";

/**
 * @title VaultToken
 * @dev Vault share certificate token, supporting accumulated yield distribution
 * @notice Inherits ERC20 standard, supports pause functionality, integrates with AccumulatedYield
 */
contract VaultToken is ERC20, Pausable, IToken, Ownable {
    // ============ State Variables ============
    address public vault;

    // Token metadata
    string private _tokenName;
    string private _tokenSymbol;
    uint8 private _tokenDecimals;

    // Initialization state
    bool private _initialized;

    // Constants
    uint8 private constant MAX_DECIMALS = 24;

    // ============ Modifiers ============

    modifier onlyVault() {
        require(msg.sender == vault, "VaultToken: only vault");
        _;
    }

    modifier whenWhitelisted(address user) {
        // Skip whitelist check for mint (from = address(0)) and burn (to = address(0)) operations
        if (user != address(0)) {
            IVault vaultContract = IVault(vault);
            if (vaultContract.whitelistEnabled()) {
                require(vaultContract.isWhitelisted(user), "VaultToken: not whitelisted");
            }
        }
        _;
    }

    modifier whenInitialized() {
        require(_initialized, "VaultToken: not initialized");
        _;
    }

    modifier whenNotInitialized() {
        require(!_initialized, "VaultToken: already initialized");
        _;
    }

    // ============ Constructor ============

    constructor() ERC20("", "") {
        // Empty constructor, supports Clones pattern
        //  Clones owner  initToken 
    }

    // ============ Initialization Function ============
    /**
     * @dev Unified initialization interface
     * @param _vault Vault address
     * @param _initData Encoded initialization data
     */
    function initiate(address _vault, bytes memory _initData) external override whenNotInitialized {
        require(_vault != address(0), "VaultToken: invalid vault");

        (string memory _name, string memory _symbol, uint8 _decimals) = 
            abi.decode(_initData, (string, string, uint8));

        _initToken(_vault, _name, _symbol, _decimals);
    }

    // ============ IToken Interface Implementation ============

    /**
     * @dev Query token name
     */
    function name() public view virtual override(ERC20, IToken) returns (string memory) {
        return _tokenName;
    }

    /**
     * @dev Query token symbol
     */
    function symbol() public view virtual override(ERC20, IToken) returns (string memory) {
        return _tokenSymbol;
    }

    /**
     * @dev Query token decimals
     */
    function decimals() public view virtual override(ERC20, IToken) returns (uint8) {
        return _tokenDecimals;
    }

    /**
     * @dev Query pause status
     */
    function paused() public view virtual override(Pausable, IToken) returns (bool) {
        return Pausable.paused();
    }



    // ============ Minting and Burning Interface ============

    /**
     * @dev Mint function
     * @param to Recipient address
     * @param amount Mint amount
     */
    function mint(address to, uint256 amount) external override onlyVault whenInitialized {
        require(to != address(0), "VaultToken: mint to zero address");
        require(amount > 0, "VaultToken: mint amount must be positive");

        _mint(to, amount);

        emit TokenMinted(to, amount);
    }

    /**
     * @dev Burn function
     * @param account Address to burn from
     * @param amount Burn amount
     */
    function burnFrom(address account, uint256 amount) external override onlyVault whenInitialized {
        require(account != address(0), "VaultToken: burn from zero address");
        require(amount > 0, "VaultToken: burn amount must be positive");
        require(balanceOf(account) >= amount, "VaultToken: insufficient balance");

        _spendAllowance(account, _msgSender(), amount);
        _burn(account, amount);

        emit TokenBurned(account, amount);
    }

    // ============ Transfer Interface ============

    /**
     * @dev transfer function, check whitelist for sender and recipient
     * @param to Recipient address
     * @param amount Transfer amount
     */
    function transfer(address to, uint256 amount) public virtual override(ERC20, IERC20) whenWhitelisted(_msgSender()) whenWhitelisted(to) returns (bool) {
        return super.transfer(to, amount);
    }

    /**
     * @dev transferFrom function, check whitelist for sender and recipient
     * @param from Sender address
     * @param to Recipient address
     * @param amount Transfer amount
     */
    function transferFrom(address from, address to, uint256 amount) public virtual override(ERC20, IERC20) whenWhitelisted(from) whenWhitelisted(to) returns (bool) {
        return super.transferFrom(from, to, amount);
    }

    // ============ Pause Control Interface ============

    /**
     * @dev Pause token transfers
     */
    function pause() external override onlyVault whenInitialized {
        if (!paused()) {
            _pause();
            emit TokenPaused();
        }
    }

    /**
     * @dev Resume token transfers
     */
    function unpause() external override onlyVault whenInitialized whenPaused {
        _unpause();

        emit TokenUnpaused();
    }



    // ============ Internal Functions ============

    /**
     * @dev Check whitelist status for a user
     * @param user User address to check
     */
    function _checkWhitelist(address user) internal view {
        IVault vaultContract = IVault(vault);
        if (vaultContract.whitelistEnabled()) {
            require(vaultContract.isWhitelisted(user), "VaultToken: not whitelisted");
        }
    }

    /**
     * @dev Initialize token (for Clones pattern)
     * @param _vault Vault contract address
     * @param _name Token name
     * @param _symbol Token symbol
     * @param _decimals Token decimals
     */
    function _initToken(
        address _vault,
        string memory _name,
        string memory _symbol,
        uint8 _decimals
    ) internal whenNotInitialized {
        require(_vault != address(0), "VaultToken: invalid vault address");
        require(bytes(_name).length > 0, "VaultToken: empty name");
        require(bytes(_symbol).length > 0, "VaultToken: empty symbol");
        require(_decimals <= MAX_DECIMALS, "VaultToken: invalid decimals");

        vault = _vault;
        _tokenName = _name;
        _tokenSymbol = _symbol;
        _tokenDecimals = _decimals;
        _initialized = true;

        // Transfer ownership to vault
        _transferOwnership(_vault);

        // Pause token trading during funding period
        _pause();
    }

    /**
     * @dev Pre-transfer checks
     */
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        super._beforeTokenTransfer(from, to, amount);

        // Check pause status (allow minting and burning during pause)
        if (from != address(0) && to != address(0)) {
            require(!paused(), "VaultToken: token transfer while paused");
        }

        // Call vault hook on token transfer
        if (from != address(0) && to != address(0)) {
            // Only call if vault is a valid contract
            if (vault.code.length > 0) {
                IVault(vault).onTokenTransfer(from, to, amount);
            }
        }
    }

    // ============ Query Interface ============

    /**
     * @dev Query if initialized
     */
    function isInitialized() external view returns (bool) {
        return _initialized;
    }


}

contracts/v2/templates/vault/BasicVault..content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.26;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "../../interfaces/IVault.sol";
import "../../interfaces/IToken.sol";

/**
 * @title BasicVault
 * @dev Basic vault template implementation, providing fundamental storage and permission management
 * @notice This contract does not contain specific business logic, business functions are implemented by other modules
 */
contract BasicVault is IVault, Ownable, ReentrancyGuard {
    // ============ State Variables ============

    address public override manager;
    bool public override whitelistEnabled;
    mapping(address => bool) public override isWhitelisted;
    address public override validator;
    bytes public override dataHash;
    bytes public override signature;

    // Cross-contract addresses
    address public accumulatedYield;
    address public override vaultToken;
    address public funding;



    // Initialization state
    bool private _initialized;

    // ============ Modifiers ============

    modifier onlyManager() {
        require(msg.sender == manager, "BasicVault: only manager");
        _;
    }

    modifier onlyFunding() {
        require(msg.sender == funding, "BasicVault: only funding");
        _;
    }

    modifier onlyVaultToken() {
        require(msg.sender == vaultToken, "BasicVault: only vault token");
        _;
    }

    modifier whenWhitelisted(address user) {
        if (whitelistEnabled) {
            require(isWhitelisted[user], "BasicVault: not whitelisted");
        }
        _;
    }

    modifier whenInitialized() {
        require(_initialized, "BasicVault: not initialized");
        _;
    }

    modifier whenNotInitialized() {
        require(!_initialized, "BasicVault: already initialized");
        _;
    }

    // ============ Constructor ============

    constructor() {
    }

    // ============ Initialization Function ============


    /**
     * @dev Unified initialization interface
     * @param _initData Encoded initialization data
     */
    function initiate(bytes memory _initData) external override whenNotInitialized {
        // decode init data
        (address _manager, address _validator, bool _whitelistEnabled, address[] memory _initialWhitelist) = 
            abi.decode(_initData, (address, address, bool, address[]));

        _initVault(_manager, _validator, _whitelistEnabled, _initialWhitelist);
    }

    // ============ IVault Interface Implementation ============

    /**
     * @dev Add address to whitelist
     * @param _addr Address to add
     */
    function addToWhitelist(address _addr) external override onlyManager whenInitialized {
        _addToWhitelist(_addr);
    }

    /**
     * @dev Remove address from whitelist
     * @param _addr Address to remove
     */
    function removeFromWhitelist(address _addr) external override onlyManager whenInitialized {
        require(isWhitelisted[_addr], "BasicVault: not whitelisted");

        isWhitelisted[_addr] = false;

        emit WhitelistRemoved(_addr);
    }

    /**
     * @dev Enable whitelist
     */
    function enableWhitelist() external override onlyManager whenInitialized {
        whitelistEnabled = true;
        emit WhitelistStatusChanged(true);
    }

    /**
     * @dev Disable whitelist
     */
    function disableWhitelist() external override onlyManager whenInitialized {
        whitelistEnabled = false;
        emit WhitelistStatusChanged(false);
    }

    /**
     * @dev Check if whitelist is enabled
     * @return Whether whitelist is enabled
     */
    function isWhiteList() external view override returns (bool) {
        return whitelistEnabled;
    }

    /**
     * @dev Verify data (simple implementation, actual applications need more complex verification logic)
     * @return Verification result
     */
    function verify() external pure override returns (bool) {
        // Simple verification logic, actual implementation needs to be based on specific requirements
        return true;
    }

    /**
     * @dev Update verification data
     * @param hash Data hash
     * @param _signature Signature data
     */
    function updateVerifyData(bytes memory hash, bytes memory _signature) external override onlyManager whenInitialized {
        dataHash = hash;
        signature = _signature;
        emit VerifyDataUpdated(hash, _signature);
    }

    /**
     * @dev Pause token
     */
    function pauseToken() external override onlyManager whenInitialized {
        require(vaultToken != address(0), "BasicVault: token not set");
        IToken(vaultToken).pause();
        emit TokenPaused();
    }

    /**
     * @dev Unpause token
     */
    function unpauseToken() external override whenInitialized {
        require(msg.sender == manager || msg.sender == funding, "BasicVault: only manager or funding");
        require(vaultToken != address(0), "BasicVault: token not set");
        IToken(vaultToken).unpause();
        emit TokenUnpaused();
    }

    /**
     * @dev Check if token is paused
     * @return Whether token is paused
     */
    function isTokenPaused() external view override returns (bool) {
        if (vaultToken == address(0)) {
            return false;
        }
        return IToken(vaultToken).paused();
    }

    /**
     * @dev Mint tokens through vault
     * @param to Recipient address
     * @param amount Amount to mint
     */
    function mintToken(address to, uint256 amount) external override onlyFunding whenInitialized whenWhitelisted(to) {
        require(vaultToken != address(0), "BasicVault: token not set");
        IToken(vaultToken).mint(to, amount);
    }

    /**
     * @dev Burn tokens through vault
     * @param from Address to burn from
     * @param amount Amount to burn
     */
    function burnToken(address from, uint256 amount) external override onlyFunding whenInitialized whenWhitelisted(from) {
        require(vaultToken != address(0), "BasicVault: token not set");
        IToken(vaultToken).burnFrom(from, amount);
    }

    /**
     * @dev Hook called on token transfer
     * @param from From address
     * @param to To address
     * @param amount Transfer amount
     */
    function onTokenTransfer(address from, address to, uint256 amount) external override whenInitialized whenWhitelisted(from) whenWhitelisted(to) {
        require(msg.sender == vaultToken, "BasicVault: only token can call");
        if (accumulatedYield != address(0) && from != address(0) && to != address(0)) {
            (bool success, ) = accumulatedYield.call(
                abi.encodeWithSignature(
                    "updateUserPoolsOnTransfer(address,address,uint256)",
                    from,
                    to,
                    amount
                )
            );
            if (!success) {
                revert("BasicVault: onTokenTransfer failed");
            }
        }
    }

    // ============ Vault Token Management ============

    function configureModules(address _vaultToken, address _funding, address _yield) external override onlyOwner whenInitialized {
        _setVaultToken(_vaultToken);
        _setFundingModule(_funding);
        _setDividendModule(_yield);
    }

    // 
    function _setVaultToken(address _vaultToken) internal {
        require(vaultToken == address(0), "BasicVault: token already set");
        require(_vaultToken != address(0), "BasicVault: invalid token address");
        vaultToken = _vaultToken;
    }
    // 
    function setVaultToken(address _vaultToken) external override onlyOwner whenInitialized {
        _setVaultToken(_vaultToken);
    }

    /**
     * @dev Set funding module address (can only be set once)
     * @param _funding Funding module address
     */
    function _setFundingModule(address _funding) internal {
        require(funding == address(0), "BasicVault: funding already set");
        require(_funding != address(0), "BasicVault: invalid funding address");
        funding = _funding;
    }
    function setFundingModule(address _funding) external override onlyOwner whenInitialized {
        _setFundingModule(_funding);
    }

    /**
     * @dev Set dividend module address (can only be set once)
     * @param _dividendModule Dividend module address
     */
    function _setDividendModule(address _dividendModule) internal {
        require(accumulatedYield == address(0), "BasicVault: dividend module already set");
        require(_dividendModule != address(0), "BasicVault: invalid dividend module address");
        accumulatedYield = _dividendModule;
    }
    function setDividendModule(address _dividendModule) external override onlyOwner whenInitialized {
        _setDividendModule(_dividendModule);
    }

    // ============ Query Functions ============

    /**
     * @dev Check if funding is successful by querying the funding module
     * @return Whether funding is successful
     */
    function isFundingSuccessful() external view override returns (bool) {
        require(funding != address(0), "BasicVault: funding module not set");

        // Call the funding module to check if funding is successful
        (bool success, bytes memory data) = funding.staticcall(
            abi.encodeWithSignature("isFundingSuccessful()")
        );

        if (!success) {
            revert("BasicVault: failed to query funding status");
        }

        return abi.decode(data, (bool));
    }

    /**
     * @dev Get funding module address
     * @return Funding module address
     */
    function getFundingModule() external view returns (address) {
        return funding;
    }

    /**
     * @dev Get dividend module address
     * @return Dividend module address
     */
    function getDividendModule() external view returns (address) {
        return accumulatedYield;
    }

    // ============ Internal Functions ============

    /**
     * @dev Internal add to whitelist function
     * @param _addr Address to add
     */
    function _addToWhitelist(address _addr) internal {
        require(_addr != address(0), "BasicVault: invalid address");
        require(!isWhitelisted[_addr], "BasicVault: already whitelisted");

        isWhitelisted[_addr] = true;

        emit WhitelistAdded(_addr);
    }

    /**
     * @dev Initialize vault (for Clones pattern)
     * @param _manager Manager address
     * @param _validator Validator address
     * @param _whitelistEnabled Whether to enable whitelist
     * @param _initialWhitelist Initial whitelist addresses
     */
    function _initVault(
        address _manager,
        address _validator,
        bool _whitelistEnabled,
        address[] memory _initialWhitelist
    ) internal whenNotInitialized {
        require(_manager != address(0), "BasicVault: invalid manager");
        require(_validator != address(0), "BasicVault: invalid validator");

        manager = _manager;
        validator = _validator;
        whitelistEnabled = _whitelistEnabled;
        _initialized = true;

        // Add initial whitelist
        for (uint256 i = 0; i < _initialWhitelist.length; i++) {
            _addToWhitelist(_initialWhitelist[i]);
        }

        _transferOwnership(_manager);
    }

    // ============ Query Interface ============

    /**
     * @dev Query if initialized
     */
    function isInitialized() external view returns (bool) {
        return _initialized;
    }
}

contracts/v2/templates/yield/Accumulated.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.26;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "../../interfaces/IAccumulatedYield.sol";
import "../../interfaces/IVault.sol";

/**
 * @title AccumulatedYield
 * @dev Accumulated yield template implementation, providing yield distribution based on token holdings
 * @notice Supports accumulated yield and real-time claiming, similar to MasterChef design
 */
contract AccumulatedYield is IAccumulatedYield, ReentrancyGuard, Ownable {
    using SafeERC20 for IERC20;

    // ============ State Variables ============

    GlobalPoolInfo public globalPool;
    mapping(address => UserInfo) public users;

    address public vault;
    address public manager;
    address public dividendTreasury;

    // Add nonce for replay protection
    uint256 public dividendNonce;

    // Precision constant
    uint256 private constant PRECISION = 1e18;

    // ============ Modifiers ============

    modifier onlyManager() {
        require(msg.sender == manager, "AccumulatedYield: only manager");
        _;
    }

    modifier onlyDividendTreasury() {
        require(msg.sender == dividendTreasury, "AccumulatedYield: only dividend treasury");
        _;
    }

    modifier onlyActivePool() {
        require(globalPool.isActive, "AccumulatedYield: pool not active");
        _;
    }

    modifier whenInitialized() {
        require(globalPool.shareToken != address(0), "AccumulatedYield: not initialized");
        _;
    }

    // ============ Constructor ============

    /**
     * @dev Constructor
     */
    constructor() {
    }

    // ============ Global Pool Management ============

    /**
     * @dev Set manager
     * @param _manager New manager address
     */
    function setManager(address _manager) external override onlyOwner whenInitialized {
        require(_manager != address(0), "AccumulatedYield: invalid manager");
        address oldManager = manager;
        manager = _manager;

        // If needed, transfer ownership to new manager
        if (owner() == oldManager) {
            _transferOwnership(_manager);
        }

        emit ManagerUpdated(oldManager, _manager);
    }

    /**
     * @dev Set dividend treasury address
     * @param _dividendTreasury New dividend treasury address
     */
    function setDividendTreasury(address _dividendTreasury) external override onlyManager whenInitialized {
        require(_dividendTreasury != address(0), "AccumulatedYield: invalid dividend treasury");
        address oldTreasury = dividendTreasury;
        dividendTreasury = _dividendTreasury;

        emit DividendTreasuryUpdated(oldTreasury, _dividendTreasury);
    }

    /**
     * @dev Unified initialization interface
     * @param _vault Vault address
     * @param _vaultToken Vault token address
     * @param _initData Encoded initialization data (contains token and original initData)
     */
    function initiate(address _vault, address _vaultToken, bytes memory _initData) external override {
        require(_vault != address(0), "AccumulatedYield: invalid vault");

        // decode initData
        (address rewardToken, address rewardManager, address dividendTreasuryAddr) = 
            abi.decode(_initData, (address, address, address));

        // init global pool 
        _initGlobalPool(_vault, rewardManager, dividendTreasuryAddr, _vaultToken, rewardToken);
    }

    /**
     * @dev Update global pool status
     * @param isActive Whether to activate
     */
    function updateGlobalPoolStatus(
        bool isActive
    ) external override onlyManager whenInitialized {
        if (isActive) {
            // require funding successful
            require(IVault(vault).isFundingSuccessful(), "AccumulatedYield: funding was not successful");
        }
        globalPool.isActive = isActive;
    }

    // ============ User Operations ============

    /**
     * @dev User claim rewards
     */
    function claimReward() external override onlyActivePool whenInitialized nonReentrant {
        // First update user pool information
        _updateUserPool(msg.sender);

        UserInfo storage user = users[msg.sender];
        uint256 currentBalance = IERC20(globalPool.shareToken).balanceOf(msg.sender);
        uint256 pending = _calculatePendingRewardAt(msg.sender, currentBalance);

        require(pending > 0, "AccumulatedYield: no pending reward");

        // Core calculation formula: reward calculation phase
        // 3: Update claimed amount
        user.totalClaimed += pending;
        user.lastClaimTime = block.timestamp;

        // Transfer rewards
        IERC20(globalPool.rewardToken).safeTransfer(msg.sender, pending);

        emit RewardClaimed(msg.sender, pending, pending, block.timestamp);
    }

    // ============ Yield Distribution ============

    /**
     * @dev Distribute dividend to global pool
     * @param dividendAmount Distribution amount
     * @param signature Dividend signature
     */
    function distributeDividend(
        uint256 dividendAmount,
        bytes memory signature
    ) external override onlyDividendTreasury onlyActivePool whenInitialized nonReentrant {
        require(dividendAmount > 0, "AccumulatedYield: invalid dividend amount");

        // Get validator address from Vault
        address validator = IVault(vault).validator();
        require(validator != address(0), "AccumulatedYield: validator not set");

        // Verify signature with nonce to prevent replay attacks
        bytes32 payload = keccak256(abi.encodePacked(vault, dividendAmount, dividendNonce));
        bytes32 ethSignedMessageHash = ECDSA.toEthSignedMessageHash(payload);

        address signer = ECDSA.recover(ethSignedMessageHash, signature);
        require(signer == validator, "AccumulatedYield: invalid signature");

        // Increment nonce to prevent replay
        dividendNonce++;

        // Transfer dividend from manager to this contract
        IERC20(globalPool.rewardToken).safeTransferFrom(msg.sender, address(this), dividendAmount);

        // Update global pool
        globalPool.totalDividend += dividendAmount;
        globalPool.lastDividendTime = block.timestamp;

        // Update total accumulated shares: shareTotalSupply * dividendAmount
        uint256 totalSupply = IERC20(globalPool.shareToken).totalSupply();

        globalPool.totalAccumulatedShares += totalSupply * dividendAmount;

        emit DividendDistributed(dividendAmount, block.timestamp, validator, signature);
    }

    // ============ Token Transfer Related ============

    /**
     * @dev Update user pools on token transfer (core functionality)
     * @param from Transfer from address
     * @param to Transfer to address
     * @param amount Transfer amount
     */
    function updateUserPoolsOnTransfer(
        address from,
        address to,
        uint256 amount
    ) external override {
        require(msg.sender == vault, "AccumulatedYield: only vault can call");

        if (from != address(0)) {
            _updateUserPool(from);
        }

        if (to != address(0)) {
            _updateUserPool(to);
        }

        emit ShareTokenTransferred(from, to, amount, block.timestamp);
    }

    // ============ Query Interface ============

    /**
     * @dev Query global pool information
     * @return Global pool information structure
     */
    function getGlobalPoolInfo() external view override returns (GlobalPoolInfo memory) {
        return globalPool;
    }

    /**
     * @dev Query user information
     * @param user User address
     * @return User information structure
     */
    function getUserInfo(address user) external view override returns (UserInfo memory) {
        return users[user];
    }

    /**
     * @dev Query user's pending rewards
     * @param user User address
     * @return Pending reward amount
     */
    function pendingReward(address user) external view override returns (uint256) {
        if (globalPool.shareToken == address(0)) {
            return 0;
        }

        uint256 currentBalance = IERC20(globalPool.shareToken).balanceOf(user);
        return _calculatePendingRewardAt(user, currentBalance);
    }

    /**
     * @dev Query global pool total dividend amount
     * @return Total dividend amount
     */
    function totalDividend() external view override returns (uint256) {
        return globalPool.totalDividend;
    }

    /**
     * @dev Query global pool total accumulated shares
     * @return Total accumulated shares
     */
    function totalAccumulatedShares() external view override returns (uint256) {
        return globalPool.totalAccumulatedShares;
    }

    /**
     * @dev Query current manager
     * @return Manager address
     */
    function getManager() external view override returns (address) {
        return manager;
    }

    /**
     * @dev Query dividend treasury address
     * @return Dividend treasury address
     */
    function getDividendTreasury() external view override returns (address) {
        return dividendTreasury;
    }

    /**
     * @dev dividendNonce
     * @return dividendNonce
     */
    function getDividendNonce() external view override onlyDividendTreasury returns (uint256) {
        return dividendNonce;
    }

    /**
     * @dev Calculate user's accumulated shares at specified balance
     * @param user User address
     * @param userBalance Specified user balance
     * @return Accumulated shares
     */
    function calculateAccumulatedShares(address user, uint256 userBalance) external view override returns (uint256) {
        if (globalPool.shareToken == address(0)) {
            return 0;
        }

        UserInfo memory userInfo = users[user];

        // Calculate dividend delta
        uint256 deltaDiv = globalPool.totalDividend - userInfo.lastClaimDividend;

        // Accumulated shares = user's current accumulated shares + specified balance * dividend delta
        uint256 accumulatedShares = userInfo.accumulatedShares + userBalance * deltaDiv;

        return accumulatedShares;
    }



    // ============ Internal Functions ============
    /**
     * @dev Initialize global yield pool
     * @param _vault Vault contract address
     * @param _manager Manager address
     * @param _dividendTreasury Dividend treasury address
     * @param shareToken Share token address
     * @param rewardToken Reward token address
     */
    function _initGlobalPool(
        address _vault,
        address _manager,
        address _dividendTreasury,
        address shareToken,
        address rewardToken
    ) internal {
        // Can only initialize once, cannot re-initialize
        require(globalPool.shareToken == address(0), "AccumulatedYield: already initialized");
        require(_vault != address(0), "AccumulatedYield: invalid vault");
        require(_manager != address(0), "AccumulatedYield: invalid manager");
        require(_dividendTreasury != address(0), "AccumulatedYield: invalid dividend treasury");
        require(shareToken != address(0), "AccumulatedYield: invalid share token");
        require(rewardToken != address(0), "AccumulatedYield: invalid reward token");

        // Set vault, manager and dividendTreasury
        vault = _vault;
        manager = _manager;
        dividendTreasury = _dividendTreasury;

        // Set owner as manager
        _transferOwnership(_manager);

        globalPool = GlobalPoolInfo({
            totalAccumulatedShares: 0,
            lastDividendTime: block.timestamp,
            totalDividend: 0,
            isActive: false,
            shareToken: shareToken,
            rewardToken: rewardToken
        });

        emit GlobalPoolInitialized(shareToken, rewardToken, block.timestamp);
    }

    /**
     * @dev Update user pool information
     * @param user User address
     */
    function _updateUserPool(address user) internal {
        if (globalPool.shareToken == address(0)) {
            return;
        }

        UserInfo storage userInfo = users[user];
        uint256 currentBalance = IERC20(globalPool.shareToken).balanceOf(user);

        // Core calculation formula: user operation phase
        // 1: Calculate dividend delta
        uint256 deltaDiv = globalPool.totalDividend - userInfo.lastClaimDividend;

        if (deltaDiv > 0) {
            // 2: Update user accumulated shares
            userInfo.accumulatedShares += currentBalance * deltaDiv;
        }

        // 3: Update user checkpoint
        userInfo.lastClaimDividend = globalPool.totalDividend;

        emit UserPoolUpdated(user, userInfo.accumulatedShares, block.timestamp);
    }



    /**
     * @dev Calculate user's pending rewards at specified balance
     * @param user User address
     * @param userBalance User balance
     * @return Pending reward amount
     */
    function _calculatePendingRewardAt(address user, uint256 userBalance) internal view returns (uint256) {
        if (globalPool.shareToken == address(0) || globalPool.totalAccumulatedShares == 0) {
            return 0;
        }

        UserInfo memory userInfo = users[user];

        // Simulate user pool update, calculate latest accumulated shares
        uint256 simulatedAccumulatedShares = userInfo.accumulatedShares;
        uint256 deltaDiv = globalPool.totalDividend - userInfo.lastClaimDividend;

        if (deltaDiv > 0) {
            simulatedAccumulatedShares += userBalance * deltaDiv;
        }

        if (simulatedAccumulatedShares == 0) {
            return 0;
        }

        // Core calculation formula: reward calculation phase with improved precision
        // 1: Calculate user's total reward with higher precision
        uint256 totalReward;
        if (globalPool.totalAccumulatedShares > 0) {
            // Use higher precision calculation to avoid precision loss
            totalReward = (simulatedAccumulatedShares * globalPool.totalDividend * PRECISION) / globalPool.totalAccumulatedShares;
            totalReward = totalReward / PRECISION; // Convert back to original precision
        } else {
            totalReward = 0;
        }

        // 2: Calculate pending reward
        uint256 pending = totalReward > userInfo.totalClaimed ? totalReward - userInfo.totalClaimed : 0;

        return pending;
    }


}
